package main

import (
    "fmt"
    "log"
    "net/http"

    "quickpoll/config"
    "quickpoll/routes"
)

func main() {
    config.Connect()

    // Jalankan migrasi setelah connect DB
    if err := config.RunMigrations(config.DB); err != nil {
        log.Fatalf("‚ùå Migration failed: %v", err)
    }

    r := routes.SetupRoutes()
    fmt.Println("üöÄ Server running on port 8080")
    log.Fatal(http.ListenAndServe(":8080", r))
}
