export default function PollResultChart({ results }) {
    if (!results || results.length === 0) return <p>Belum ada suara.</p>;

    return (
        <div className="mt-6">
            <h3 className="font-bold mb-2">Hasil Voting</h3>
            <ul>
                {results.map((res, index) => (
                    <li key={index} className="mb-2">
                        <div className="flex justify-between">
                            <span>{res.option_text}</span>
                            <span>{res.total_votes} suara</span>
                        </div>
                        <div className="w-full bg-gray-200 h-2 rounded mt-1">
                            <div
                                style={{
                                    width: `${res.total_votes > 0 ? (res.total_votes / results.reduce((acc, r) => acc + r.total_votes, 0)) * 100 : 0}%`,
                                    backgroundColor: '#3b82f6',
                                    height: '8px',
                                    borderRadius: '999px',
                                }}
                            ></div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
